mapper.select:
select u.user_id, u.user_name, u.nick_name, u.sex, u.orgnization, u.job, u.phonenumber, u.email, u.direction, u.user_remark,u.password,u.avatar,u.status,u.del_flag, u.login_ip, u.login_date, u.create_by, u.create_time, u.remark, org.organization_name from sys_tuser u left join sys_user_role ur on u.user_id = ur.user_id left join sys_role r on r.role_id = ur.role_id left join sys_organization org on org.organization_id = u.orgnization where u.del_flag = '0' AND u.user_name != "admin" AND u.user_name != "master" AND u.status !="2" AND u.status !="3"
AND r.role_id = 4
AND u.user_id != #{userId}
AND u.user_name like concat('%', #{userName}, '%')
AND u.nick_name like concat('%', #{nickName}, '%')
AND u.orgnization = #{orgnization}
AND org.organization_name like concat('%', #{orgnizationName}, '%')
AND u.direction like concat('%', #{direction}, '%')
AND u.status = #{status}
AND u.phonenumber like concat('%', #{phonenumber}, '%')
AND date_format(u.create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')
AND date_format(u.create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')
${params.dataScope}

select distinct u.user_id, u.user_name, u.nick_name, u.email, u.phonenumber, u.status, u.create_time from sys_tuser u left join sys_user_role ur on u.user_id = ur.user_id left join sys_role r on r.role_id = ur.role_id where u.del_flag = '0' and r.role_id = #{roleId}
AND u.user_name like concat('%', #{userName}, '%')
AND u.phonenumber like concat('%', #{phonenumber}, '%')
${params.dataScope}

mapper.update:
update sys_tuser
user_name = #{userName}, nick_name = #{nickName}, sex = #{sex}, orgnization = #{orgnization}, job = #{job}, phonenumber = #{phonenumber}, email = #{email}, direction = #{direction}, user_remark = #{userRemark}, password = #{password}, avatar = #{avatar}, status = #{status}, login_ip = #{loginIp}, login_date = #{loginDate}, update_by = #{updateBy}, remark = #{remark}, update_time = sysdate()
where user_id = #{userId}
