mapper.select:
SELECT a.apply_id apply_id, a.apply_state apply_state, a.apply_content apply_content, a.apply_time apply_time, a.apply_dispose_time apply_dispose_time, t.team_id team_id, t.team_name team_name, u.user_id user_id, u.user_name user_name, u.nick_name nick_name, t.team_state team_state,t.team_headcount team_headcount,c.competition_people_sum competition_people_sum, c.competition_id competition_id, c.competition_name competition_name FROM t_apply a LEFT JOIN sys_suser u ON u.user_id = a.user_id LEFT JOIN t_team t ON t.team_id = a.team_id LEFT JOIN t_competition c ON c.competition_id = t.competition_id WHERE a.team_id IN
#{item.teamId}
AND (a.apply_state = 1 OR a.apply_state = 2) ORDER BY a.apply_dispose_time DESC

SELECT a.apply_id apply_id, a.apply_state apply_state, a.apply_content apply_content, a.apply_time apply_time, a.apply_dispose_time apply_dispose_time, t.team_id team_id, t.team_name team_name, u.user_id user_id, u.user_name user_name, u.nick_name nick_name, t.team_state team_state,t.team_headcount team_headcount,c.competition_people_sum competition_people_sum, c.competition_id competition_id, c.competition_name competition_name FROM t_apply a LEFT JOIN sys_suser u ON u.user_id = a.user_id LEFT JOIN t_team t ON t.team_id = a.team_id LEFT JOIN t_competition c ON c.competition_id = t.competition_id WHERE a.team_id IN
#{item.teamId}
AND a.apply_state = 0 ORDER BY a.apply_dispose_time DESC
